            }

            // Validate date ranges for any over lap of current address with previous addresses.
            if (currentAddressStartDateEpoch &amp;&amp; currentAddressEndDateEpoch) {
              if (!(currentAddressStartDateEpoch &gt; prevAddressDateRangeList[i].endDateEpoch ||
                currentAddressEndDateEpoch &lt; prevAddressDateRangeList[i].startDateEpoch)) {
